import React, { Component } from 'react'
import { Text, View, StyleSheet, TextInput, ScrollView, TouchableHighlight,
		ActivityIndicator, FlatList} from 'react-native'

import { Avatar, ListItem, SearchBar} from "react-native-elements"

import Icon from 'react-native-vector-icons/FontAwesome';

import { NavigationEvents } from 'react-navigation'

import { connect } from 'react-redux'
import { changeLoadView, changeStackScreenPropsFunction } from '../../redux/actions/popUpActions'
import { ListOfFriends_Texts } from '../../languages/screens/contactUsers/ListOfFriends'

import * as Log          from '../../genericFunctions/logFunctions'
import * as Database     from '../../genericFunctions/databaseFunctions'
import { genericStyles, WHITE_COLOR } from '../../constants/genericStyles'

import * as auxiliarFunctions from '../../genericFunctions/otherFunctions'
import {DEFAULT_USER_ICON} from '../../constants/genericImages'

import PouchDB from 'pouchdb-react-native'


class ListOfFriends extends Component {

	constructor(props) {
		super(props)

		this.state = {
			loading:            true, // Variable que determina si se están cargando los amigos desde la base de datos
			loadFunctionOpened: true, // Indica si se puede ingresar a la función loadFriends
			filter_name:        null, // Almacenará el la cadena de caracteres que ingrese el usuario para filtrar la búsqueda de usuarios
			friendsList:        null, // Lista de todos los amigos del usuario actual
		}
	}

	// Formato de la cabecera de la vista
	static navigationOptions = ({ screenProps }) => ({
		title: ListOfFriends_Texts[screenProps.stackNavigation.getScreenProps().language][0],
		drawerIcon: () => (<Icon  name="users"  color="black"  size={20}/>)
	});

	// Cargamos los amigos del usuario actual desde la base de datos, y los almacenamos en una variable de estado local
	loadFriends = async() => {
		var friendsList = [];
		const db = new PouchDB('lithodex');

		await db.get('default')
			.then(async(database) => {  
				// Identificadores de usuario de los amigos del actual
				let friends_ids = database.users[this.props.user_id].friends; 

				// Aquí obtenemos la lista de amigos, sólo manteniendo las propiedades necesarias de cada uno, para no sobrecargar la memoria
				var friendsList = [];
				var keysList    = Object.keys(friends_ids);
				for (i = 0; i < keysList.length; i++){
					let user = database.users[keysList[i]];
					friendsList.push({_id: user._id, userName: user.userName, profileImage: user.information.profileImage})
				}

				// Ordenamos la lista de amigos
				friendsList = friendsList.sort((a, b) => (a.userName.toLowerCase() > b.userName.toLowerCase()) ? 1 : -1);

				this.setState({friendsList, loading: false});

			}).catch(error => {
				console.error(error.toString());
				this.setState({loading: false})
			})
		await this.props.dispatchChangeLoadView(false);
		// Esto hace que el botón de tres líneas que está en la parte derecha de la cabecera pueda abrir el DrawerNavigator
		this.props.dispatchStackScreenPropsFunction(this.props.navigation.openDrawer);
		this.setState({loadFunctionOpened: true});
	}

	// Por si acaso desmontamos esta vista antes de que a la función de cargar desde la base de datos le dé tiempo
	// de despachar esta variable como falsa
	componentWillUnmount(){
		this.props.dispatchChangeLoadView(false);
	}

	// Para ir a la vista del usuario que se va a examinar
	goToUserView = (user_id) => {
		let payload = {_id: user_id}
		this.props.screenProps.stackNavigation.navigate({key: 'UserView', routeName: 'UserView', params: payload});
	}

	// Una vez que se cambia el texto en el que se filtran los nombres, se invoca este procedimiento
	setFilter = (text) => {
		this.setState({filter_name: text})
	}

	// Función para mostrar los usuarios como una lista de botones
	renderFriends(filter_name){
		let s = this.state;
		let myList = (filter_name == null) ? s.friendsList : s.friendsList.filter(item => auxiliarFunctions.stringIncludesSubstring_NoStrict(item.userName,filter_name));
		return(
			<FlatList
				ItemSeparatorComponent = {Platform.OS !== 'android' && (({ highlighted }) => (
					<View // Esto aparentemente servirá cuando trabajemos con iOS
						style = {[
							style.separator,
							highlighted && { marginLeft: 0 }
						]}
					/>
				))}
				data         = {myList} // Arreglo con los elementos que queremos listar
				keyExtractor = {(item) => item._id.toString()} // Sirve para indicarle que lo que debe usar como "key" por cada elemento es el atributo "_id"
				renderItem   = {({ item, index, separators }) => (
					<TouchableHighlight
						key            = {item.key}
						onPress        = {() => this.goToUserView(item._id)}
						onShowUnderlay = {separators.highlight}
						onHideUnderlay = {separators.unhighlight}
					>
						<ListItem
							title      = {item.userName}
							key        = {index}
							leftAvatar = {
								<Avatar
									size   = "medium"
									source = {(item.profileImage != null) ? {uri: item.profileImage} : DEFAULT_USER_ICON}
								/>
							}
						/>
					</TouchableHighlight>
				)}
			/>
		)
	}

	// Sirve para activar la función que lee desde la base de datos. Esto es útil cuando estamos emulando la aplicación y refrescamos la página
	// en caliente, ya que hacer eso volverá a colocar this.state.loading en su valor inicial (true) pero este componente ya estará montado, por lo que 
	// no se activará el NavigationEvents onWillFocus, y en consecuencia la vista se quedará pegada en "Cargando"
	activateLoadFriends(){
		if (this.state.loadFunctionOpened){
			this.setState({loadFunctionOpened: false});
			this.loadFriends();
		}
		return(<View/>)
	}

	// Lo que se le mostrará al usuario
	render() {
		let s = this.state;
		let p = this.props;

		// Caso en que todavía no se ha cargado la información desde la base de datos
		if (s.loading){
			return(
				<View style = {{ flex: 1, alignItems: 'center', justifyContent: 'flex-start'}}>
					<View style = {{ flex: 1, alignItems: 'center', justifyContent: 'center', flexDirection: 'column'}}>
						{this.activateLoadFriends()}
						<ActivityIndicator size = "large" color = "#0000ff" />
						{/*Mensaje: "Cargando"*/}
						<Text>{p.allMessages[1]}...</Text>
					</View>
				</View>
			)
		}

		/// Caso en que ya se han cargado los usuarios
		return (
			<View style = {genericStyles.lightGray_background}>
				{/* Caso en que regresamos de otra vista del DrawerNavigator*/}
				{(!p.loadView) && <NavigationEvents onWillFocus = {payload => this.loadFriends()}/>}

				{/*Caso en que regresamos de una vista del StackNavigator*/}
				{p.loadView && this.activateLoadFriends()}

				{(s.friendsList.length != 0) &&
					< // Parte en la que el usuario ve la lista de otros usuarios
					View style = {{...genericStyles.white_background_without_ScrollView, flex: 1}}>
						{/*Aquí el usuario puede filtrar la búsqueda del otro usuario a buscar*/}
						<SearchBar
							value                  = {s.filter_name}
							selectTextOnFocus      = {true}
							lightTheme             = {true}
							textAlign              = {'center'} 
							inputStyle             = {{color: 'black', backgroundColor: WHITE_COLOR}}
							placeholder            = {p.allMessages[2]} // Mensaje: "Buscar amigos..."
							placeholderTextColor   = {'gray'}
							onChangeText           = {text => this.setFilter(text)}
						/>

						{/*En esta parte debe mostrarse la lista de usuarios*/}
						<View style = {localStyles.userPicker}>
							{this.renderFriends(s.filter_name)}
						</View>
					</View>
				}

				{(s.friendsList.length == 0) &&
					<View style = {{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
						{/*Mensaje: "No se han añadido amigos todavía"*/}
						<Text style = {{fontSize: 14}}>{p.allMessages[3]}</Text>
					</View>
				}
			</View>
		); 
	}
}

/// Constante para darle formato a los diversos componentes de esta pantalla
const localStyles = StyleSheet.create({

	// Formato para mostrar un usuario (no la lista completa, sino uno específico)
	userPicker: {
		flex:          8,
		flexDirection: 'column',
		padding:       10,
	},

});

// Función para obtener las variables deseadas desde el estado global de la tienda Redux
const mapStateToProps = (state) => {
	return {
		allMessages: ListOfFriends_Texts[state.appPreferencesReducer.language], 
		user_id:     state.userReducer.user_id,
		loadView:    state.popUpReducer.loadView,
	}
};

const mapDispatchToProps = (dispatch) => {
	return {
		dispatchChangeLoadView: (bool) => dispatch(changeLoadView(bool)),
		dispatchStackScreenPropsFunction: (globalFunction) => dispatch(changeStackScreenPropsFunction(globalFunction)),
	}
};

export default connect(mapStateToProps, mapDispatchToProps)(ListOfFriends);
