	// Ventana principal
	render() {
		let s = this.state;
		let p = this.props;

		return (
			<View>

				{/*Modales*/}
				{this.Modal_1_View()} 
				{this.Modal_2_View()}
				{this.Modal_3_View()}
				{this.Modal_4_View()}

				{/*sta es la parte que ve el usuario cuando est en la ventana externa*/}

				{ // Caso en que no se ha seleccionado ninguna litologa y se est haciendo una captura del afloramiento
				!s.savedLithology && p.takingShot &&
					<View style = {{width: D.LITHOLOGY_PICKER_WIDTH, height: p.height, borderWidth: 1, borderColor: 'black'}}/>
				}

				{ // Caso en que no se ha seleccionado ninguna litologa, la altura es menor que 18 y no se est haciendo captura del afloramiento
				!s.savedLithology && (p.height < 18) && (!p.takingShot) &&
					<TouchableHighlight onPress={()=>{this.showModal_1(true)}}  style={{width: D.LITHOLOGY_PICKER_WIDTH, height: p.height}}>
						<View style = {localStyles.showInstructionsObjectScreen}/>
					</TouchableHighlight>
				}

				{ /// Caso en que no se ha seleccionado ninguna litologa, la altura mayor o igual que 18 y no se est haciendo captura del afloramiento
				!s.savedLithology && (p.height >= 18) && (!p.takingShot) &&
					<TouchableHighlight onPress={()=>{this.showModal_1(true)}}  style={{width: D.LITHOLOGY_PICKER_WIDTH, height: p.height}}>
						<View style = {localStyles.showInstructionsObjectScreen}>
							{/*Mensaje: "(Toque para cambiar la litologa)"*/}
							<Text>{p.allMessages[21]}</Text>
						</View>
					</TouchableHighlight>
				}

				{ /// Caso en que ya se seleccion una litologa
				s.savedLithology && 
					<TouchableHighlight onPress={()=>{this.showModal_1(true)}}  style={{width: D.LITHOLOGY_PICKER_WIDTH, height: p.height}}>
						<View style = {{ 
							flex:            1, 
							flexDirection:   'row', 
							justifyContent:  'flex-start', 
							alignItems:      'center',  
							backgroundColor: "#"+s.savedHexadecimalColor,
							width:           (s.savedIsCarbonate) ? (80 + s.savedGrainDiameter * D.LITHOLOGY_ADDING_TERM) : (50 + s.savedGrainDiameter * D.LITHOLOGY_ADDING_TERM),     
						}}>
							<Image 
								source       = {s.savedLithology.uri} 
								resizeMethod = "auto"
								style        = {{
									width:       (s.savedIsCarbonate) ? (80 + s.savedGrainDiameter * D.LITHOLOGY_ADDING_TERM) : (50 + s.savedGrainDiameter * D.LITHOLOGY_ADDING_TERM),
									height:      p.height, 
									opacity:     0.5, 
									borderColor: 'black', 
									borderWidth: 1,
								}}
							/>
						</View>
					</TouchableHighlight>
				} 
			</View>
		);
	}
}

