import React, { Component } from 'react';
import { StyleSheet, Text, View, Image, ActivityIndicator,
	    Alert, Button, ScrollView, Picker } from 'react-native';

import {Settings_Texts} from '../languages/screens/Settings'
import * as Log         from '../genericFunctions/logFunctions'
import * as Database    from '../genericFunctions/databaseFunctions'
import {genericStyles}  from '../constants/genericStyles'
import PouchDB          from 'pouchdb-react-native'

import { connect } from 'react-redux'
import { changeLanguage } from '../redux/actions/appPreferencesActions'
import { changeUser, changeUserPrivileges } from '../redux/actions/userActions'
import { changeLithologyListLanguage, changeStructureListLanguage, changeFossilListLanguage,
         changeNoCarbonatesRuleLanguage, changeCarbonatesRuleLanguage } from '../redux/actions/libraryActions'


class Settings extends Component {

	constructor(props){
		super(props)
	}

	// Formato de la cabecera de la vista
	static navigationOptions = ({ screenProps }) => ({
		title:           Settings_Texts[screenProps.language][11],
		headerTintColor: screenProps.headerTintColor,
		headerStyle: {
			backgroundColor: screenProps.headerBackgroundColor,
			...genericStyles.navigationHeader,
		}
	});

	// Para registrar en el "log" que se ha ingresado en el menú de configuración
	componentDidMount() {
		Log.log_action(2, this.props.user_id);    
	}

	// Procedimiento para cambiar el idioma
	updateLanguage = (newLanguage) => {
		let p = this.props;
		p.dispatchChangeLanguage(newLanguage); // Actualizamos el idioma en el estado global que se mantiene en la tienda de Redux
		p.dispatchChangeLithologyListLanguage(newLanguage); // Actualizamos la lista de litologías debido al nuevo idioma
		p.dispatchChangeStructureListLanguage(newLanguage); // Actualizamos la lista de estructuras sedimentarias debido al nuevo idioma  
		p.dispatchChangeFossilListLanguage(newLanguage); // Actualizamos la lista de estructuras sedimentarias debido al nuevo idioma
		p.dispatchChangeNoCarbonatesRuleLanguage(newLanguage); // Actualizamos la regla de los no carbonatos debido al nuevo idioma
		p.dispacthChangeCarbonatesRuleLanguage(newLanguage); // Actualizamos la regla de los carbonatos debido al nuevo idioma
	}

	// Procedimiento para borrar la base de datos del usuario (sólo afloramientos y núcleos; no se borra su información personal)
	deletePersonalDatabase = () => {
		let p = this.props;	

		// Procedimiento auxiliar que se invoca cuando se confirma que se desea eliminar la base de datos personal
		let deletePersonalDatabaseAux = (p) => {
			Database.deleteUserData(p.user_id);
			Alert.alert(p.allMessages[12], p.allMessages[0]);
		}

		// Alerta: "¿Seguro de que desea eliminar su base de datos personal?"
		Alert.alert(p.allMessages[12], p.allMessages[15],
			[
				// Mensaje: "Sí"
				{text: p.allMessages[13], onPress: () => deletePersonalDatabaseAux(p)},
				// Mensaje: "No"
				{text: p.allMessages[14]},
			] 
		)
	}

	// Procedimiento para que un usuario se borre a sí mismo de la base de datos
	deleteOwnUserFromDatabase = () => {
		let p = this.props;	

		// Procedimiento auxiliar que se invoca cuando se confirma que se desea eliminar el usuario del sistema
		let deleteOwnUserFromDatabaseAux = (p) => {
			Database.deleteOwnUser(p.user_id);
			Alert.alert(p.allMessages[12], p.allMessages[10]);

			p.dispatchResetUser('unauthenticated');
			p.dispatchResetUserPrivileges();
			p.navigation.goBack();
		}

		// Alerta: "¿Seguro de que desea eliminar su usuario del sistema?"
		Alert.alert(p.allMessages[12], p.allMessages[16],
			[
				// Mensaje: "Sí"
				{text: p.allMessages[13], onPress: () => deleteOwnUserFromDatabaseAux(p)},
				// Mensaje: "No"
				{text: p.allMessages[14]},
			] 
		)
	}

	// Procedimiento para borrar la base de datos completa. Disponible sólo para el administrador
	deleteCompleteDatabase = () => {
		let p = this.props;	

		// Procedimiento auxiliar que se invoca cuando se confirma que se desea eliminar la base de datos completa
		let deleteCompleteDatabaseAux = (p) => {
			new PouchDB('lithodex').destroy().
				then(function () {
					Database.dummy_database();
					Alert.alert(p.allMessages[12], p.allMessages[0]);
				}).catch(function (error) {
					console.error(error.toString())
				})
			p.dispatchResetUser('unauthenticated');
			p.dispatchResetUserPrivileges();
			p.navigation.goBack();
		}

		// Alerta: "¿Seguro de que desea eliminar la base de datos en su totalidad?"
		Alert.alert(p.allMessages[12], p.allMessages[17],
			[
				// Mensaje: "Sí"
				{text: p.allMessages[13], onPress: () => deleteCompleteDatabaseAux(p)},
				// Mensaje: "No"
				{text: p.allMessages[14]},
			] 
		)
	}

	// Lo que se le mostrará al usuario
	render (){
		let p = this.props;

		return (
			<View>
				<ScrollView>

					{/*Sección para seleccionar el idioma en el que se muestran los textos en la aplicación*/}
					<View style = {styles.container}>
						{/*Mensaje: "Seleccionar idioma"*/}
						<Text style = {styles.instructionTitle}>{p.allMessages[1]}</Text>
						<Picker
							selectedValue = {p.language}
							style         = {{height: 30, width: '31.25%'}}
							onValueChange = {(itemValue) => this.updateLanguage(itemValue)}
						>
							<Picker.Item label = {p.allMessages[2]}  value = {'spanish'}/>
							<Picker.Item label = {p.allMessages[3]}  value = {'english'}/>
						</Picker>
			      	</View>

					{/*Sección para borrar toda la base de datos, incluyendo a los otros usuarios. Sólo lo puede hacer el administrador*/}
					<View style = {styles.container}>
						{/*Mensaje: "Borrar base de datos personal"*/}
						<Text style = {styles.instructionTitle}>{p.allMessages[7]}</Text>
						<Button 
							title   = {p.allMessages[5]} /// Mensaje: "Borrar base de datos"
							color   = 'red'
							onPress = {() => this.deletePersonalDatabase()}
						/>
					</View>

					{/*Sección para eliminar su propio usuario. El usuario no autenticado no puede borrarse a sí mismo, ni tampoco el administrador primario, 
					   que es el que viene por defecto en la base de datos, puesto que siempre necesitamos que haya al menos un administrador */}
					{ (p.user_id != '===?¡18jswi..__q173x5gbxjf(()459jdcj') && (p.user_id != 'unauthenticated') &&
						<View style = {styles.container}>
							{/*Mensaje: "Eliminar usuario propio de la base de datos"*/}
							<Text style = {styles.instructionTitle}>{p.allMessages[8]}</Text>
							<Button 
								title   = {p.allMessages[9]} /// Mensaje: "Eliminar usuario"
								color   = 'red'
								onPress = {() => this.deleteOwnUserFromDatabase()}
							/>
						</View>
					}

					{/*Sección para borrar toda la base de datos, incluyendo a los otros usuarios. Sólo lo puede hacer el administrador*/}
					{ (p.privileges == 2) &&
						<View style = {styles.container}>
							{/*Mensaje: "Borrar base de datos completa"*/}
							<Text style = {styles.instructionTitle}>{p.allMessages[4]}</Text>
							<Button 
								title   = {p.allMessages[5]} /// Mensaje: "Borrar base de datos"
								color   = 'red'
								onPress = {() => this.deleteCompleteDatabase()}
							/>
						</View>
					}
		      	</ScrollView>
      		</View>
		);
	}
}

/// Variable para darle formato a los diversos componentes de esta pantalla
const styles = StyleSheet.create({

	// Empleado para dividir por secciones cada una de las funciones de esta vista.
	container: {
		height:         110,
		alignItems:     'center',
		justifyContent: 'center',
	},

	// Empleado para mostrar el texto que indica el tipo de instrucción
	instructionTitle: {
		alignItems:     'flex-start',
		justifyContent: 'flex-start',
		fontWeight:     'bold',
		fontSize:       17,
		padding:        10,
		paddingTop:     20,
		textAlign:      'center'
	},

});

// Función para obtener las variables deseadas desde el estado global de la tienda Redux
const mapStateToProps = (state) => {
	return {
		language:    state.appPreferencesReducer.language,
		allMessages: Settings_Texts[state.appPreferencesReducer.language],
		user_id:     state.userReducer.user_id,
		privileges:  state.userReducer.privileges,
	}
};

const mapDispatchToProps = (dispatch) => {
	return {
		dispatchChangeLanguage: (language) => dispatch(changeLanguage(language)),
		dispatchResetUser:      (user_id)  => dispatch(changeUser(user_id)),
		dispatchResetUserPrivileges: ()    => dispatch(changeUserPrivileges(0)),
		dispatchChangeLithologyListLanguage:    (language) => dispatch(changeLithologyListLanguage(language)),
		dispatchChangeStructureListLanguage:    (language) => dispatch(changeStructureListLanguage(language)),   
		dispatchChangeFossilListLanguage:       (language) => dispatch(changeFossilListLanguage(language)),
		dispatchChangeNoCarbonatesRuleLanguage: (language) => dispatch(changeNoCarbonatesRuleLanguage(language)), 
		dispacthChangeCarbonatesRuleLanguage:   (language) => dispatch(changeCarbonatesRuleLanguage(language)), 
	}
};

export default connect(mapStateToProps, mapDispatchToProps)(Settings)
